export function Logo({
  size = 'md',
  showText = true,
  variant = 'default',
  className = '',
  href = '/'
}: LogoProps) {
  const config = sizeConfig[size];

  // Color variants
  const colors = {
    default: { primary: '#2C4FF8', text: '#323131' },
    white: { primary: '#FFFFFF', text: '#FFFFFF' },
    mono: { primary: '#323131', text: '#323131' },
  };

  const { primary, text } = colors[variant];

  const content = showText ? (
    <LogoFull
      height={config.height}
      primaryColor={primary}
      textColor={text}
      className={className}
    />
  ) : (
    <LogoIcon
      size={config.iconSize}
      primaryColor={primary}
      className={className}
    />
  );

  if (href) {
    return (
      <Link href={href} className="hover:opacity-90 transition-opacity inline-flex">
        {content}
      </Link>
    );
  }

  return content;
}

// Export icon-only version for convenience
export function LogoMark({
  size = 'md',
  variant = 'default',
  className = '',
  href = '/'
}: Omit<LogoProps, 'showText'>) {
  return (
    <Logo
      size={size}
      variant={variant}
      showText={false}
      className={className}
      href={href}
    />
  );
}

export default Logo;
